mapper.update:
update  board_tbl
set meet = #{meet}, membernum=#{memberNum}, contact=#{contact}, period=#{period}, title=#{title}, content=#{content}, deadline=#{deadline}
where bno = #{bno}

mapper.select:

SELECT bno, deadline,meet, memberNum,regdate, period, contact,content, uidkey ,title, uname, fnames, snames, cnt, replycnt, NVL(profileImage, 'profileLogo.png') AS profileImage
FROM (select * from board_view_01)
WHERE bno=#{bno}


SELECT bno, deadline, uidkey ,title, uname, fnames, snames, cnt, replycnt, NVL(profileImage, 'profileLogo.png') AS profileImage
FROM (select * from board_view 
WHERE 



 rownum <= #{pageNum} * #{amount}
 )
WHERE rn > (#{pageNum} - 1) * #{amount}


SELECT bno, deadline, uidkey ,title,content, uname, fnames, snames, cnt, replycnt, NVL(profileImage, 'profileLogo.png') AS profileImage
FROM (
	select * from board_view 
	WHERE



	 rownum <= #{pageNum} * #{amount}
)
WHERE rn > (#{pageNum} - 1) * #{amount}


		AND snames IN (

			'%'||#{filter}||'%'

		)

SELECT bno, deadline, uidkey ,title, uname, fnames, snames, cnt, replycnt, NVL(profileImage, 'profileLogo.png') AS profileImage
FROM (select * from board_view)
where uidkey=#{uidkey}
